var newD = 'playjjnow.online', // новый домен
	oldD = ['alloeclub.com', 'allohalive.com', 'allohastream.com', 'thealloha.club', 'newplayjj.com', 'playjjnow.online'], // список старых доменов + новый
	port = ':9443', // порт который должен быть указан у домена
	delay = 200;
replace();
function replace() {
	setTimeout(replace, delay++);
	Array.prototype.find.call(document.body.getElementsByTagName("iframe"), function (elem) {
		var src = elem.src,
			p = '';
		oldD.forEach((item) => {
			if (src.includes(item)) {
				if (src.includes(port) && item == newD) return;
				if (!src.includes(port)) p = port;
				src = src.replace(/:\d{2,6}/, '');
				elem.src = src.replace(item, newD + p);
			}
		});
	});
}
